<Layout categories={categories} vidcat={vidcat}>
    <div className="Greeting">

      <a className="paddingSpace"></a>
        <h1 className="Title">{landingpage.HeaderTitle}</h1>
        <hr></hr>

    </div>
    <div className="ImmageHolder">

        <Image image={landingpage.DisplayImage} style={style}></Image>
        <div className="APlaceTo">
            <h3 className="GetReadyTo" >Get Ready to: 
                <TextLoop interval={650}> 
                    {purposes.map((purpose, i) => {
                  return (
