import Articles from "../../components/articles";
import { fetchAPI } from "../../lib/api";
import Layout from "../../components/layout";
import Seo from "../../components/seo";

const Category = ({category, categories, vidcat }) => {
  const seo = {
    metaTitle: category.Name,
    metaDescription: `All ${category.Name} videos`,
  };

  return (
    <Layout categories={categories} vidcat={vidcat}>
      <Seo seo={seo} />
      <div className="uk-section">
        <div className="uk-container uk-container-large">
          <h1 className="Title">{category.Name}</h1>
          {/* <Articles articles={category.articles} /> */}
        </div>
      </div>
    </Layout>
  );
};

export async function getStaticPaths() {
  const categories = await fetchAPI("/vid-catagories");

  return {
    paths: categories.map((category) => ({
      params: {
        slug: category.slug,
      },
    })),
    fallback: false,
  };
}

export async function getStaticProps({ params }) {
  const category = (await fetchAPI(`/vid-catagories?slug=${params.slug}`))[0];
  const categories = await fetchAPI("catagories");
  const vidcat = await fetchAPI("/vid-catagories");

  return {
    props: { category,  categories, vidcat },
    revalidate: 1,
  };
}

export default Category;
