import React from "react";
import Articles from "../components/articles";
import Layout from "../components/layout";
import Seo from "../components/seo";
import { fetchAPI } from "../lib/api";
import { BrowserRouter, Route, Switch } from 'react-router-dom';


const Articals = ({ articles, categories, homepage, global, landingpage }) => {

  let showLanding = false;

  OnlyArticals:{
    <div>
    <Layout categories={showLanding, categories}>
      <Seo seo={homepage.seo} />
        <div className="uk-section">
        <div className="uk-container uk-container-large">
        {/* <LandingPage></LandingPage> */}
          <h1 className="Title">{homepage.hero.title}</h1>
          <Articles articles={articles} />
        </div>
      </div>
    </Layout>
  </div>
  }

  if(showLanding){
    return (
      <h1>Landing!!!</h1>
    );
  }else if(!showLanding){
    return (

      <div>
      <Layout categories={showLanding, categories}>
        <Seo seo={homepage.seo} />
          <div className="uk-section">
          <div className="uk-container uk-container-large">
          {/* <LandingPage></LandingPage> */}
            <h1 className="Title">{homepage.hero.title}</h1>
            <Articles articles={articles} />
          </div>
        </div>
      </Layout>
    </div>

    );
  }

};

export async function getStaticProps() {
  // Run API calls in parallel
  const [articles, categories, homepage, global, landingpage, writers, showLanding] = await Promise.all([
    fetchAPI("/articles?status=published"),
    fetchAPI("/categories"),
    fetchAPI("/homepage"),
    fetchAPI("/global"),
    fetchAPI("/landing-page"),
    fetchAPI("/writers"),
    showLanding = true
  ]);

  return {
    props: { articles, categories, homepage, global, landingpage, writers, showLanding},
    revalidate: 1,
  };
}

export default Articals;
