import React from "react";
import Articles from "../components/articles";
import Layout from "../components/layout";
import Seo from "../components/seo";
import { fetchAPI } from "../lib/api";
import SearchExampleStandard from "../components/search"

const Home = ({ articles, categories, homepage, global }) => {
  return (

    <div>
    <Layout categories={categories}>
    <h1 className="SiteName">{landingpage.greeting}</h1>

      <Seo seo={homepage.seo} />
        <div className="uk-section">
        <div className="uk-container uk-container-large">
          <h1 className="Title">{homepage.hero.title}</h1>
          <Articles articles={articles} />
        </div>
      </div>
    </Layout>
  </div>
  );
};

export async function getStaticProps() {
  // Run API calls in parallel
  const [articles, categories, homepage, global, landingpage] = await Promise.all([
    fetchAPI("/articles?status=published"),
    fetchAPI("/categories"),
    fetchAPI("/homepage"),
    fetchAPI("/global"),
    fetchAPI("/landing-page")
  ]);

  return {
    props: { articles, categories, homepage, global, landingpage },
    revalidate: 1,
  };
}

export default Home;
