import React from "react";
import Articles from "../components/articles";
import Layout from "../components/layout";
import Seo from "../components/seo";
import { fetchAPI } from "../lib/api";
import { BrowserRouter, Route, Switch } from 'react-router-dom';


const Articals = ({ articles, categories, homepage, global, landingpage }) => {
  return (

    <div>
    <Layout categories={categories}>
      <Seo seo={homepage.seo} />
        <div className="uk-section">
        <div className="uk-container uk-container-large">
        {/* <LandingPage></LandingPage> */}
          <h1 className="Title">{homepage.hero.title}</h1>
          <Articles articles={articles} />
        </div>
      </div>
    </Layout>
  </div>
  );
};

export async function getStaticProps() {
  // Run API calls in parallel
  const [articles, categories, homepage, global, landingpage, writers] = await Promise.all([
    fetchAPI("/articles?status=published"),
    fetchAPI("/categories"),
    fetchAPI("/homepage"),
    fetchAPI("/global"),
    fetchAPI("/landing-page"),
    fetchAPI("/writers")

  ]);

  return {
    props: { articles, categories, homepage, global, landingpage, writers },
    revalidate: 1,
  };
}

export default Home;
